document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("theme-toggle"),t=document.documentElement,d=e.querySelector(".fa-moon"),s=e.querySelector(".fa-sun");"dark"===localStorage.getItem("theme")?(t.classList.add("dark"),d.classList.add("hidden"),s.classList.remove("hidden")):(t.classList.remove("dark"),d.classList.remove("hidden"),s.classList.add("hidden")),e.addEventListener("click",(()=>{t.classList.toggle("dark"),t.classList.contains("dark")?(localStorage.setItem("theme","dark"),d.classList.add("hidden"),s.classList.remove("hidden")):(localStorage.setItem("theme","light"),d.classList.remove("hidden"),s.classList.add("hidden"))}))}));